Dashmix.onLoad(
  class {
    static initValidation() {
      Dashmix.helpers("jq-validation"),
        jQuery(".js-validation-reminder").validate({
          rules: { "reminder-credential": { required: !0, minlength: 3 } },
          messages: {
            "reminder-credential": {
              required: "Please enter a valid credential",
            },
          },
        });
    }
    static init() {
      this.initValidation();
    }
  }.init()
);
